mapper.select:
SELECT sg.goodsTitle,sg.goodsType,sg.goodsUnit,sg.goodsModel,sc.codeDesc, a.* from swcmes_store a left join swcmes_goods sg on sg.goodsNo = a.goodsNo left join swcmes_code sc on sc.codeNo = a.locateCode where a.attrib not like 'XXX%' and suppNo is null
and a.goodsNo = #{goodsNo}
order by goodsType, regDate, modDate

SELECT ss.suppTitle,ss.suppType,ss.suppUnit,ss.suppModel,sc.codeDesc,a.* from swcmes_store a left join swcmes_supp ss on ss.suppNo = a.suppNo left join swcmes_code sc on sc.codeNo = a.locateCode where a.attrib not like 'XXX%' and goodsNo is null
and a.suppNo = #{suppNo}
order by suppType, regDate, modDate

mapper.update:
update swcmes_store
	set 
	inoutTyp = #{inoutTyp}
	,goodsNo = #{goodsNo}
	,suppNo = #{suppNo}
	,storeUnit = #{storeUnit}
	,storeQty = #{storeQty}
	,locateCode = #{locateCode}
	,modDate = now()
	,comment = #{comment}
	where storeioNo = #{storeioNo}
